$main-color:  #FF4343;
$second-color: #2D3033;
$text-color: #6C7279;

@mixin btn {
    padding: 28px 0;
    background: $main-color;
    color: #FFF;
    text-align: center;
    font-size: 24px;
    line-height: 28px;
    font-weight: 900;
    text-transform: uppercase;
    display: block;

    &:hover {
        background: rgba(231, 20, 20, 1);
        transition: all 0.3s;
    }

    &:active {
        box-shadow: 4px 4px 30px 0px #FF4343;
        transition: all 0.3s;
    }

    @media (max-width: 767px) {
        padding: 18px 0;
    }
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;

    font-family: 'Raleway';
    font-style: normal;
    font-weight: 400;
}

a {
    text-decoration: none;
}

.container {
    max-width: 1200px;
    padding: 0 15px;
    margin: 0 auto;

    @media (max-width: 1024px) {
        max-width: 1014px;
        padding: 0 15px;
    }
}


.header {
    padding: 36px 0;
    position: fixed;
    top: 0;
    left: 50%;
    transform: translate(-50%, 0);
    width: 100%;
    z-index: 2;

    @media (max-width: 767px) {
        padding: 21.4px 0;
    }

    &::after {
        content: "";
        width: 1170px;
        height: 1px;
        display: block;
        position: absolute;
        background: #BABBC5;
