        }
    }

    &__wrapper {
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    &__logo-wrapper {
        max-width: 183px;
        display: inline-block;

        @media (max-width: 767px) {
            max-width: 103px;
        }
    }

    &__logo {
        width: 100%;
        height: 100%;
        object-fit: cover;
    }

    &__burger-wrapper {
        max-width: 24px;
        display: none;

        @media (max-width: 890px) {
            display: block;
        }

        &__burger {
            width: 100%;
            height: 100%;
            object-fit: cover;

            @media (max-width: 890px) {
                fill: $main-color;
            }
        }

        // $__burger-line:hover {
