    }

    .nav__list {
        display: flex;
        list-style: none;
        column-gap: 4px;

        .nav__item {
            min-width: 130px;
            text-align: center;
            position: relative;
        }

        .nav__link {
            display: inline-block;
            font-weight: 700;
            font-size: 13px;
            line-height: 19px;
            text-align: center;
            text-transform: uppercase;
            color: #FFFFFF;

            @media (max-width: 1024px) {
                font-size: 12px;
            }
        }

        .nav__link:active {
            color: #E71414;
            transition: all 0.3s;
        }

        .nav__item-active::after,
        .nav__item:hover::after {
            content: "";
            width: 100%;
            height: 3px;
            left: 0;
            top: 100%;
            background: #BABBC5;
            position: absolute;
            transition: all 0.3s;
